canonical_solution:
import java.util.*;

public class Solution {
    public static int[] digits(int n) {
        String s = Integer.toString(Math.abs(n));
        int[] result = new int[s.length()];
        for (int i = 0; i < s.length(); i++) {
            result[i] = s.charAt(i) - '0';
        }
        return result;
    }
}

